-- Insert all property data from CSV into the properties table
-- Using ON CONFLICT to update if property already exists

ON CONFLICT (id) DO UPDATE SET
  area = EXCLUDED.area,
  title_en = EXCLUDED.title_en,
  title_ms = EXCLUDED.title_ms,
  description_en = EXCLUDED.description_en,
  description_ms = EXCLUDED.description_ms,
  furnishing_en = EXCLUDED.furnishing_en,
  furnishing_ms = EXCLUDED.furnishing_ms,
  price = EXCLUDED.price,
  sqft = EXCLUDED.sqft,
  bedroom = EXCLUDED.bedroom,
  move_in_date = EXCLUDED.move_in_date,
  agent_phone = EXCLUDED.agent_phone,
  image_url = EXCLUDED.image_url,
  map_url = EXCLUDED.map_url,
  updated_at = CURRENT_TIMESTAMP;
